            $.each(makeModelYear, function (key, val) {

                if ( !makes.includes(val.make) ) {
                    makes.push(val.make);
                }
            });
        }

        function getModels(make) {

            makeModelYear.filter(function (item) {

                if ( item.make === make ) {

                    if ( !models.includes(item.model) ) {
                        models.push(item.model);
                    }
                }
            });
        }

        function getYears(make, model) {

            makeModelYear.filter(function (item) {

